import {createContext} from 'react'
import {useState} from 'react'


const CartContext = createContext(); 

const CartProvider = ({children}) => {
    const carta = []
    const [cambio, setCambio] = useState()


    const addItem = (item) => {

        if (!isInCart(item.id)) {
            carta.push(item)


        } else if (isInCart(item.id)) {
            console.log("Ya esta en el carrito!")
            console.log(carta)

        }

    }

    const removeItem = (item) => {
        console.log(item)

        const posItem = carta.findIndex(el => el.id === item.id)
        console.log(posItem)

        if (isInCart(item.id)) {
            carta.splice(posItem,1);
            setCambio(true)

        } else {
            setCambio(false)
        }
    }

    const clear = () => {
        carta.splice(0,carta.length)

    }

    const isInCart = (id) => {
        return carta.some((el) => el.id === id)

    }



    const data = {addItem, removeItem, clear, isInCart, carta}
    return <CartContext.Provider value={data}>{children} </CartContext.Provider>
}

export {CartProvider}
export default CartContext
