import { useContext, useState, useEffect } from "react"
import CartContext from "../Context/CartContext"
import ItemCount from "../Components/ItemCount"
import trash from "../Assets/trash-fill.svg"



function Cart() {

    const { addItem, removeItem, clear, cart} = useContext(CartContext)
    const [total, setTotal] = useState(0)
    const [cantidad, setCantidad] = useState(0)

    function onAdd(numero) {
        setCantidad(numero);
    }

  function remove(e,libro) {
      removeItem(libro)
      setTimeout(() => {
          libro.cantidad = 0;

      }, 500);


  }

  useEffect(() => {

    const suma = cart.reduce((previus,current) => previus + (current.valor * current.cantidad), 0)

    setTotal(suma)
  }, [cart])







    return (

        <div className=" h-[30rem] w-[90%] ml-20 bg-white my-7 static overflow-auto rounded-xl ">

            <table className="table-auto border-separate border-spacing-1 w-full border pt-2 overflow-scroll">
                <thead>
                    <tr >
                        <th >Articulo</th>
                        <th>Precio</th>
                        <th>Cantidad</th>

                    </tr>
                </thead>
                <tbody className="text-center ">
                    {cart
                    .map((libro) => 
                          <tr key={libro.id}>
                          <td>{libro.nombre}</td>
                          <td>$ {libro.valor} US </td>
                          <td><ItemCount stock={libro.stock} funcion={onAdd} alt="invert" initial = {libro.cantidad}/></td>
                          <td><img src={trash} onClick={(e) => remove(e,libro)} className="ml-20 h-5 cursor-pointer"></img></td>
                      </tr>
                    )}








                </tbody>




            </table>


            <div className="flex items-center justify-around bg-slate-500  flex-grow h-16  w-[90%] absolute bottom-0 mb-10 rounded-b-xl">


                <div className="flex items-center">
                    <h2 className="text-white text-2xl font-sans mx-10">Total: </h2>
                    <h1 className="text-2xl ml-2 text-white hover:underline hover:animate-pulse cursor-none">{total}</h1>

                </div>
                <button className=" bg-red-600 text-white rounded-xl p-3 hover:bg-red-700 ">Continuar</button>
            </div>
        </div>
    )



}


export default Cart
