import { useContext, useState } from "react"
import CartContext from "../Context/CartContext"
import ItemCount from "../Components/ItemCount"
import trash from "../Assets/trash-fill.svg"



function Cart() {

    const { addItem, removeItem, clear, cart } = useContext(CartContext)

    const [cantidad, setCantidad] = useState(0)

    function onAdd(numero) {
        setCantidad(numero);
    }

console.log(cart)


    return (

        <div className=" h-[30rem] w-[90%] ml-20 bg-white my-5 static overflow-auto">

            <table className="table-auto border-separate border-spacing-1 w-full border pt-2 overflow-scroll">
                <thead>
                    <tr >
                        <th >Articulo</th>
                        <th>Precio</th>
                        <th>Cantidad</th>

                    </tr>
                </thead>
                <tbody className="text-center ">
                    {cart.map((libro) => {
                          <tr>
                          <td>{libro.nombre}</td>
                          <td>{libro.valor}</td>
                          <td><ItemCount funcion={onAdd} alt="invert" initial = {0}/></td>
                          <td><img src={trash} className="ml-20 h-5 cursor-pointer"></img></td>
                      </tr>
                    })}








                </tbody>




            </table>


            <div className="flex items-center justify-center bg-slate-600  flex-grow h-16  w-[90%] absolute bottom-0 mb-10 "><span>Footer 6</span></div>
        </div>
    )



}


export default Cart
