import {createContext} from 'react'


const CartContext = createContext(); 

const CartProvider = ({children}) => {
    const cart = []


    const addItem = (item) => {

        console.log(isInCart(1))
        if (isInCart(item.id)) {
            cart.push(item)
            console.log("se deberia agregar el item!" + cart)
        } else if (!isInCart(item.id)) {
            console.log(cart)

            return false;
        }

    }

    const removeItem = (item) => {

        if (isInCart(item.id) === true ) {
            cart.splice(item.id,1);
        } else {
            return false;
        }
    }

    const clear = () => {
        cart.splice(0,cart.length)

    }

    const isInCart = (id) => {
        cart.some( el => el.id === id)

    }

    const data = {addItem, removeItem, clear, isInCart, cart}
    return <CartContext.Provider value={data}>{children} </CartContext.Provider>
}

export {CartProvider}
export default CartContext
