  const listado = () => {
    let items = require("./productos.json")
    return new Promise ((resolve, reject) => {
        setTimeout(() => {
            resolve(items)
        }, 200000);
    })
  }

  async function fetchedItems(){
    const items = await listado(); 
    setCartas(items)
  }

  useEffect(() => {
    setCartas(<Spinner/>)
    fetchedItems();
  })


