import React from 'react'
import { useState } from 'react'


export default function ItemCount({ stock, initial }) {
    const [value, setValue] = useState(initial);
    const [estado, setEstado] = useState("");

    function onAdd() {
        (stock) !== value ? setValue(value + 1) : setEstado("¡Stock Superado!");
    }

    function onSubstract() {
        setEstado("");
        value !== 0 ? setValue(value - 1) : setEstado("");
    }

    return (
        <div>
            <h1 className='text-white font-bold mt-2'>Stock Actual: {stock}</h1>
            <div className='flex justify-center'>
                <button className="bg-slate-600 h-5 w-5 rounded my-2 mx-3" onClick={onSubstract}><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512"><path d="M432 256c0 17.7-14.3 32-32 32L48 288c-17.7 0-32-14.3-32-32s14.3-32 32-32l352 0c17.7 0 32 14.3 32 32z"/></svg></button>
                <h1>{value}</h1>
                <button className="bg-slate-600 h-5 w-5 rounded my-2 mx-3" onClick={onAdd}><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512"><path d="M256 80c0-17.7-14.3-32-32-32s-32 14.3-32 32V224H48c-17.7 0-32 14.3-32 32s14.3 32 32 32H192V432c0 17.7 14.3 32 32 32s32-14.3 32-32V288H400c17.7 0 32-14.3 32-32s-14.3-32-32-32H256V80z"/></svg></button>
            </div>

            <h1>{estado}</h1>
        </div>
    );

}
