        <div className="flex flex-wrap justify-start mt-20">
          {!loading ? cartas 
          : categoria ? cartas
          .filter((libro) => libro.categoria === categoria)
          .map((el)=>(

            <Item 
            key={el.id}
            nombre={el.nombre}
            calificacion={el.calificacion}
            autor={el.autor}
            img = {el.img}
            categoria = {el.categoria}
            reseña = {el.reseña}
            id={el.id}

            />
          ))
        : cartas
        .map((el)=>(

          <Item 
          key={el.id}
          nombre={el.nombre}
          calificacion={el.calificacion}
          autor={el.autor}
          img = {el.img}
          categoria = {el.categoria}
          reseña = {el.reseña}
          id={el.id}

          />
        ))
        }
        </div>

    );
}
