      <div className="inline-flex absolute -top-2 -right-2 justify-center items-center w-6 h-6 text-xs font-bold text-white bg-red-500 rounded-full border-2 border-white dark:border-gray-900">{cart.lentgh}</div>
      </img>
    </NavLink>



  );
}

export default CartWidget;
